plugins {
    application
}

repositories {
    mavenCentral()
}

dependencies {
    implementation("org.glassfish.jersey.containers:jersey-container-jdk-http:2.46")
    implementation("org.glassfish.jersey.inject:jersey-hk2:2.46")
    implementation("org.glassfish.jaxb:jaxb-runtime:2.3.6")
    implementation("org.glassfish.jaxb:txw2:2.3.6")
    implementation("com.sun.activation:jakarta.activation:1.2.2")
}

tasks.jar {
    manifest {
        attributes(
            "Main-Class" to "com.caoccao.glassfish.demo.Demo"
        )
    }
}

tasks.withType<JavaCompile> {
    options.encoding = "UTF-8"
}
